        // lade Sonderbuchungen
        var isClosingDay = (!DigiWebApp.BookingController.currentBooking);
        var mySonderbuchungen = _.filter(DigiWebApp.Sonderbuchung.find(), function(n) { return !parseBool(n.get("uebertragen")) });
